sp_widget.client_script:
function DoctorListController($location, $scope, spUtil) {
  var c = this;

  // Initialize
  c.loading = true;
  c.error   = null;
  c.doctors = [];

  // When data is received from server
  $scope.$on('data_received', function() {
    c.loading = false;
    c.error   = c.data.error || null;

  });

  // Kick off the fetch from the server
  spUtil.get($scope);

  // Your selectDoctor, etc.
  c.selectDoctor = function(sysId) {
    $location.path('/doctor_portal')
             .search({ id: 'doctor_profile', sys_id: sysId });
  };
}
DoctorListController.$inject = ['$location', '$scope', 'spUtil'];

