sp_widget.script:
(function() {
  // pick up your table name
  var tableName = options.tableName || 'x_1032697_easy_a_0_u_doctor';

  // data.* always has to exist
  data.doctors = [];
  data.count = 0;

  // standard GlideRecord query
  var gr = new GlideRecord(tableName);
  gr.addQuery('u_active', true);
  gr.orderBy('u_name');
  gr.query();

  // manual counter
  while (gr.next()) {
    data.doctors.push({
      sys_id:    gr.getValue('sys_id'),
      name:      gr.getValue('u_name'),
      specialty: gr.getDisplayValue('u_specialty')
    });
    data.count++;
  }

  // optional debug
  gs.info("DoctorListWidget: loaded " + data.count + " records");
})();
